mapper.select:
SELECT convert(char(19), t.updatetime, 120) as updatetime,t.exclusivefg,t.orgcdid,t.orgname,t.orgshortname,t.regionid, dict1.busidictname as regioniddict, t.address,t.tel,t.fax,t.zipcode,convert(char(10), t.effectivedate, 23) as effectivedate,ROW_NUMBER() OVER(order by t.regionid,t.updatetime desc) as rownum FROM [dbo].[s_organize] t left join [dbo].[s_busidict] dict1 on t.regionid = dict1.busidictid and dict1.busidicttypeid = 'OA_COMMON_Region' where t.deletefg = '0'
and t.regionid = #{regionid}
and t.orgname like '%'+#{orgname}+'%'

mapper.update:

        update s_organize set
           updator = #{updator, jdbcType=VARCHAR}
           ,updatetime = #{updatetime, jdbcType=TIME}
           ,exclusivefg = #{updateexclusivefg, jdbcType=VARCHAR}
           ,orgid = #{orgid, jdbcType=VARCHAR}
           ,regionid = #{regionid, jdbcType=VARCHAR}
           ,orgname = #{orgname, jdbcType=VARCHAR}
           ,orgshortname = #{orgshortname, jdbcType=VARCHAR}
           ,address = #{address, jdbcType=VARCHAR}
           ,tel = #{tel, jdbcType=VARCHAR}
           ,fax = #{fax, jdbcType=VARCHAR}
           ,zipcode = #{zipcode, jdbcType=VARCHAR}
           ,effectivedate = #{effectivedate, jdbcType=DATE}
    	where orgcdid = #{orgcdid, jdbcType=VARCHAR} and deletefg = '0'           


			and exclusivefg = #{exclusivefg , jdbcType=VARCHAR}


        update s_organize set
           updator = #{updator, jdbcType=VARCHAR}
           ,updatetime = #{updatetime, jdbcType=TIME}
           ,exclusivefg = #{updateexclusivefg, jdbcType=VARCHAR}
           ,orgid = #{orgid, jdbcType=VARCHAR}
           ,deletefg = '1'
    	where orgcdid = #{orgcdid, jdbcType=VARCHAR} and deletefg = '0'           


			and exclusivefg = #{exclusivefg , jdbcType=VARCHAR}
