mapper.select:
SELECT convert(char(19), t.processtime, 120) as processtime, t.applyid, t.applyno, t.exclusivefg, t.applyempid, t1.empname as applyempname, t.processempid, t2.empname as processempname, t.applytype, dict1.busidictname as applytypedict, t.status, dict2.busidictname as statusdict, ROW_NUMBER() OVER(order by t.processtime desc, t.applyno ) as rownum FROM [dbo].[t_applyform] t inner join [dbo].[t_empinfo] t1 on t.applyempid = t1.empid and t.deletefg = t1.deletefg inner join [dbo].[t_empinfo] t2 on t.processempid = t2.empid and t.deletefg = t2.deletefg left join [dbo].[s_busidict] dict1 on t.applytype = dict1.busidictid and dict1.busidicttypeid = 'OA_PC001_ApplyType' left join [dbo].[s_busidict] dict2 on t.status = dict2.busidictid and dict2.busidicttypeid = 'OA_PD001_Status' where t.deletefg = '0' and t.status in (5,6,7)
and t.personfilecheckid = #{empid}
and t.applyno like '%'+#{applyno}+'%'
and t.applytype = #{applytype}
and t.status = #{status}

mapper.update:

        update t_applyform set
           updator = #{updator, jdbcType=VARCHAR}
           ,updatetime = #{updatetime, jdbcType=TIME}
           ,exclusivefg = #{updateexclusivefg, jdbcType=VARCHAR}
           ,orgid = #{orgid, jdbcType=VARCHAR}
           ,processempid = #{processempid, jdbcType=VARCHAR}
           ,processtime = #{processtime, jdbcType=TIME}          
           ,status = #{status, jdbcType=VARCHAR}      
    	where applyid = #{applyid, jdbcType=VARCHAR} and deletefg = '0'           


			and exclusivefg = #{exclusivefg , jdbcType=VARCHAR}
