mapper.select:

select v.empid as busidictid, v.empname as busidictname  from [dbo].[v_orgdepposemp] v 
where v.orgcdid = #{orgcdid} and v.depid = #{depid}
	order by v.depid, v.posid


SELECT 
	count(*) 
FROM [dbo].[t_empinfo] t where deletefg = '0' and t.username = #{username}


	and t.empid != #{empid}


SELECT 
	count(*) 
FROM [dbo].[s_busidicttype] t where t.busidicttypeid = #{typeid}


	and t.busidicttypeid != #{busidicttypeid}

select count(*) from [dbo].[t_eventdevice] t1 inner join [dbo].[t_eventtime] t2 on t1.eventid = t2.eventid and t1.deletefg = t2.deletefg inner join [dbo].[t_event] t3 on t1.eventid = t3.eventid and t1.deletefg = t3.deletefg where t3.status != '9' and t1.deletefg = '0'
and t1.eventid != #{eventid}
and t1.dailydeviceid = #{deviceid}
and ( (t2.eventstarttime < CONVERT(datetime, #{start}, 20) and t2.eventendtime > CONVERT(datetime, #{start}, 20)) or (t2.eventstarttime < CONVERT(datetime, #{end}, 20) and t2.eventendtime > CONVERT(datetime, #{end}, 20)) or (t2.eventstarttime >= CONVERT(datetime, #{start}, 20) and t2.eventendtime <= CONVERT(datetime, #{end}, 20)) )


select count(*)  
from [dbo].[t_dailyplan] t1
where t1.deletefg = '0'


	and t1.eventid != #{eventid}


	and t1.empid = #{id}


	and ( 
		(t1.dailystarttime < CONVERT(datetime, #{start}, 20) and t1.dailyendtime > CONVERT(datetime, #{start}, 20)) or 
		(t1.dailystarttime < CONVERT(datetime, #{end}, 20) and t1.dailyendtime > CONVERT(datetime, #{end}, 20)) or 
		(t1.dailystarttime >= CONVERT(datetime, #{start}, 20) and t1.dailyendtime <= CONVERT(datetime, #{end}, 20))
	)

mapper.update:

        update t_applyform set
           updator = #{updator, jdbcType=VARCHAR}
           ,updatetime = #{updatetime, jdbcType=TIME}
           ,exclusivefg = #{updateexclusivefg, jdbcType=VARCHAR}
           ,orgid = #{orgid, jdbcType=VARCHAR}
           ,processempid = #{processempid, jdbcType=VARCHAR}
           ,processtime = #{processtime, jdbcType=TIME}          
           ,status = #{status, jdbcType=VARCHAR}      
    	where applyid = #{applyid, jdbcType=VARCHAR} and deletefg = '0'           


			and exclusivefg = #{exclusivefg , jdbcType=VARCHAR}
