mapper.select:
SELECT t.applyid, t.exclusivefg, t.sourceid, t.applyno, t.applyempid, t1.empname as applyempname, t.processempid, t2.empname as processempname, convert(char(19), t.processtime, 120) as processtime, t.managercheckid, managercheck.empname as managercheckname, t.managerorgcdid, managerorgcd.orgshortname as managerorgcdname, t.managerdepid, t.personnelcheckid, personnelcheck.empname as personnelcheckname, t.personnelorgcdid, personnelorgcd.orgshortname as personnelorgcdname, t.personneldepid, t.vicepresicheckid, vicepresicheck.empname as vicepresicheckname, t.vicepresiorgcdid, vicepresiorgcd.orgshortname as vicepresiorgcdname, t.vicepresidepid, t.presicheckid, presicheck.empname as presicheckname, t.presiorgcdid, presiorgcd.orgshortname as presiorgcdname, t.presidepid, t.personfilecheckid, personfilecheck.empname as personfilecheckname, t.personfileorgcdid, personfileorgcd.orgshortname as personfileorgcdname, t.personfiledepid, t.applytype, t.vacatereasontype, dict1.busidictname as vacatereasontypedict, t.otherremark, t.applyreason, convert(char(10), t.applystart, 23) as applystart, convert(char(10), t.applyend, 23) as applyend, t.applystarthm, t.applyendhm, convert(char(19), t.applystarttime, 120) as applystarttime, convert(char(19), t.applyendtime, 120) as applyendtime, t.extraworkapplytype, t.evectionworkflag, convert(char(10), t.extraworkstart, 23) as extraworkstart, convert(char(10), t.extraworkend, 23) as extraworkend, t.extraworkstarthm, t.extraworkendhm, convert(char(19), t.extraworkstarttime, 120) as extraworkstarttime, convert(char(19), t.extraworkendtime, 120) as extraworkendtime, t.evectionaddress, dict2.busidictname as evectionaddressdict, t.evectioncountry, t.evectionprovince, t.evectioncity, t.evectionaddress1, t.evectionaddress2, t.evectionconnects, t.evectionstart, t.airplaneflag, t.totalhours, t.evectionmoney, t.evectionallowance, t.totalmoney, t.status, t.checklevel, t.remark, t3.unlegalvctn,t3.unwealvctn,t3.unextraworkvctn,source1.applyno as sourceapplyno FROM [dbo].[t_applyform] t left join [dbo].[s_busidict] dict1 on t.vacatereasontype = dict1.busidictid and dict1.busidicttypeid = 'OA_PC001_VacReasonType' left join [dbo].[s_busidict] dict2 on t.evectionaddress = dict2.busidictid and dict2.busidicttypeid = 'OA_PC001_EVAddress' left join [dbo].[t_empinfo] t1 on t.applyempid = t1.empid and t.deletefg = t1.deletefg left join [dbo].[t_empinfo] t2 on t.processempid = t2.empid and t.deletefg = t2.deletefg left join [dbo].[t_annualvctn] t3 on t.applyempid = t3.empid and t.deletefg = t3.deletefg and t3.year = (select substring(CONVERT(varchar(100), GETDATE(), 21),1,4) as sysdate) left join [dbo].[t_empinfo] managercheck on t.managercheckid = managercheck.empid and t.deletefg = managercheck.deletefg left join [dbo].[s_organize] managerorgcd on t.managerorgcdid = managerorgcd.orgcdid and t.deletefg = managerorgcd.deletefg left join [dbo].[t_empinfo] personnelcheck on t.personnelcheckid = personnelcheck.empid and t.deletefg = personnelcheck.deletefg left join [dbo].[s_organize] personnelorgcd on t.personnelorgcdid = personnelorgcd.orgcdid and t.deletefg = personnelorgcd.deletefg left join [dbo].[t_empinfo] vicepresicheck on t.vicepresicheckid = vicepresicheck.empid and t.deletefg = vicepresicheck.deletefg left join [dbo].[s_organize] vicepresiorgcd on t.vicepresiorgcdid = vicepresiorgcd.orgcdid and t.deletefg = vicepresiorgcd.deletefg left join [dbo].[t_empinfo] presicheck on t.presicheckid = presicheck.empid and t.deletefg = presicheck.deletefg left join [dbo].[s_organize] presiorgcd on t.presiorgcdid = presiorgcd.orgcdid and t.deletefg = presiorgcd.deletefg left join [dbo].[t_empinfo] personfilecheck on t.personfilecheckid = personfilecheck.empid and t.deletefg = personfilecheck.deletefg left join [dbo].[s_organize] personfileorgcd on t.personfileorgcdid = personfileorgcd.orgcdid and t.deletefg = personfileorgcd.deletefg left join [dbo].[t_applyform] source1 on t.sourceid = source1.applyid and t.deletefg = source1.deletefg where t.deletefg = '0'
and t.applyid = #{applyid}
and t.exclusivefg = #{exclusivefg}

SELECT convert(char(19), t.updatetime, 120) as updatetime,t.exclusivefg,t.empid,t.year,t1.empname, t.legalvctn,t.wealvctn,t.extraworkvctn,t.haslegalvctn,t.haswealvctn,t.hasextraworkvctn, t.unlegalvctn,t.unwealvctn,t.unextraworkvctn, ROW_NUMBER() OVER(order by t.updatetime desc) as rownum FROM [dbo].[t_annualvctn] t inner join [dbo].[t_empinfo] t1 on t.empid = t1.empid and t.deletefg = t1.deletefg where t.deletefg = '0'
and t.empid = #{empid}
and t.year = #{year}
and t.year = (select substring(CONVERT(varchar(100), GETDATE(), 21),1,4) as sysdate)
and t.exclusivefg = #{exclusivefg}

select t.resourceid, t.resourcename, t.resourcecode, t.isleaf, t.resourcelevel, t.resourceaction, t.parentid, t1.resourcename as parentname, t.resourcetype, t.sortno, t.remark1 from [dbo].[v_empresource] v inner join [dbo].[s_resource] t on v.resourceid = t.resourceid left join [dbo].[s_resource] t1 on t.parentid = t1.resourceid where 1 = 1
and v.empid = #{empid}
and t.resourcelevel = #{resourcelevel}
and t.parentid = #{parentid}
order by t.resourcelevel, t.sortno

select t.resourceid, t.resourcename, t.resourcecode, t.isleaf, t.resourcelevel, t.resourceaction, t.parentid, t1.resourcename as parentname, t.resourcetype, t.sortno, t.remark1 from [dbo].[v_empresource] v inner join [dbo].[s_resource] t on v.resourceid = t.resourceid left join [dbo].[s_resource] t1 on t.parentid = t1.resourceid where 1=1
and v.empid = #{empid}
and t.resourcelevel = #{resourcelevel}
and t.parentid = #{parentid}
order by t.resourcelevel, t.sortno

