mapper.select:
SELECT t1.dailydeviceid,t1.orgcdid,org.orgname,org.orgshortname,t1.dailydevicename, t1.comment,V1.daily, V1.eventstarttime, V1.eventendtime, ROW_NUMBER() OVER(order by t1.orgcdid,t1.dailydevicename,V1.daily desc,V1.eventstarttime) as rownum FROM [dbo].[t_dailydeviceinfo] t1 left join [dbo].[s_organize] org on t1.orgcdid = org.orgcdid and t1.deletefg = org.deletefg left join ( select t2.dailydeviceid, convert(char(10), t4.daily, 23) as daily,convert(char(19), t4.eventstarttime, 120) as eventstarttime, convert(char(19), t4.eventendtime, 120) as eventendtime from [dbo].[t_eventdevice] t2 inner join [dbo].[t_event] t3 on t2.eventid = t3.eventid and t2.deletefg = t3.deletefg inner join [dbo].[t_eventtime] t4 on t2.eventid = t4.eventid and t2.deletefg = t4.deletefg where t3.status != '9' and t2.deletefg = '0'
and convert(char(10), t4.daily, 23) = #{displaydate}
) V1 on t1.dailydeviceid = V1.dailydeviceid where t1.deletefg = '0'
and (V1.daily = #{displaydate} or V1.daily is null)
and t1.dailydevicename like '%'+#{devicename}+'%'
and t1.orgcdid in ${item}

SELECT t.dailydeviceid,t.orgcdid,org.orgname,org.orgshortname,t.dailydevicename, t.comment,ROW_NUMBER() OVER(order by t.orgcdid,t.dailydevicename) as rownum FROM [dbo].[t_dailydeviceinfo] t left join [dbo].[s_organize] org on t.orgcdid = org.orgcdid where t.deletefg = '0'
and t.dailydevicename like '%'+#{dailydevicename}+'%'
and t.orgcdid in ${item}

SELECT t.orgcdid, t.orgname, t.orgshortname, t.regionid, dict1.busidictname as regioniddict, ROW_NUMBER() OVER(order by t.regionid,t.orgname) as rownum FROM [dbo].[s_organize] t left join [dbo].[s_busidict] dict1 on t.regionid = dict1.busidictid and dict1.busidicttypeid = 'OA_COMMON_Region' where t.deletefg = '0' order by t.sortno
and t.regionid = #{regionid}
and t.orgname = #{orgname}
